INSERT INTO `data` (`id`, `ARKTIKUL`, `CODE`, `NAME`, `CENA_ROZN`, `CENA_OPT`, `URL`, `OSTATOK`, `GROUP`, `OPISANIE`, `GARANT`, `IMPORT`, `EAN`, `PROIZV`, `STRANA`, `MATERIAL`, `CVET`, `DLINA`, `DIAM`, `IST`, `UPAK`, `CENA_IM`, `category_id`) VALUES
	(1595, 'RE005', 'ъ01845', '*Nexus REVO STEALTH вибро-массажер простаты с вращающейся головкой', 13490, 6790, 'http://kazanova.su/files/foto/RE005.jpeg', 5, 'Массажеры простаты', 'Усовершенствованная модель лучшего массажера простаты! Уникальная мужская игрушка - массажер, не только полезный для мужского здоровья, но и приносящий массу совершенно новых приятных ощущений. Теперь еще более удобное управление и еще более разнообразный массаж! Пульт управления имеет лаконичную форму и действует на расстоянии до 15 метров. Идеальная форма для мужской анальной стимуляции: уникальная разработка, благодаря которой головка стимулятора вращается отдельно, стимулируя простату. Дополнительная стимуляция промежности осуществляется рельефным основанием массажера с шипиками, которые будут щекотать в такт вибрации. Массажер невероятно прост в уходе - после использования достаточно просто вымыть его теплой мыльной водой, и он снова готов к использованию. Nexus Revo оснащен удивительно сильным, но при этом тихим моторчиком. Усиленный внутренний пластиковый каркас для большей надежности. Брызгоустойчивый корпус. 2 режима вращения и 6 программ вибрации (медленная, средняя, быстрая, слабая пульсация, сильная пульсация и возрастающая) - сочетайте их так, как вам больше нравится! Nexus Revo STEALTH не нуждается в батарейках - вы можете в любой момент зарядить его с помощью входящего в комплект зарядного устройства - всего 3,5 часа подзарядки подарят до 10 часов наслаждения. Материал: Усиленный пластик  медицинский силикон. Размеры: Общая высота: 14,5 см, глубина введения 10 см, ширина основания 12,65 см., диаметр до 3,2 см. В комплект входит универсальный USB-кабель, который позволит зарядить устройство от ПК. Используйте со смазкой на водной основе. Внимание: не погружать в воду!', '1 год', 0, '5060274220721', 'Nexus, Великобритания', 'Китай', 'силикон', 'черный', '13 - 15 см', '2 - 4 см', 'аккумулятор (usb-адаптер в комплекте)', 'картонная коробка', 13490, 78);
